//获取繁简转换设置
var v=source.getVariable();
//判断设置是否为空
if(v==null||v==""){
	v=1;
	source.setVariable(v);
	java.toast("未设置目录排序，默认目录升序。");
	}else{v=v};
//保存设置
function set(num){
	msg=num==1||0?
	"目录升序！":"目录降序！";
	tips="\n刷新后生效。"
	java.longToast("设置成功：\n"+
		msg+tips);
	return source.setVariable(num);
	}
//获取设置
function get(v){
	msg=v==1||0?
	"目录升序！":"目录降序！";
	java.longToast("当前状态：\n"+msg);
	return;
}
//按设置执行
var tzs=v==1?true:false;
tzs?java.t2s(result):result;

