@js:
var data=result.match(/<\/dt>[^@]*<\/dt>([^@]*)<\/dl>/)[1];//只取有用的部分
var datalist=data.split("<dd>");//数据切开
var list=[],len=datalist.length;
if(len>1){
	for(var i=1;i<len;i++){
		var temp=datalist[i].match(/<a href="(.*)">(.*)<\/a>/);
		list.push({
			"chapterName":temp[2],
			"chapterUrl":temp[1]
		});
	}
}
list;

