var data=result.match(/<\/dt>[^@]*<\/dt>([^@]*)<\/dl>/)[1];//åªå–æœ‰ç”¨çš„éƒ¨åˆ†
var datalist=data.split("<dd>");//æ•°æ®åˆ‡å¼€
var list=[],len=datalist.length;
if(len>1){
	for(var i=1;i<len;i++){
		var temp=datalist[i].match(/<a href="(.*)">(.*)<\/a>/);
		list.push({
			"chapterName":temp[2],
			"chapterUrl":temp[1]
		});
	}
}
list;

