function formatChapter(index, title) {
  // å¦‚æœä¼ å…¥çš„æ˜¯å­—ç¬¦ä¸²æ•°å­—ï¼ˆå¦‚ "23"ï¼‰ï¼Œå…ˆè½¬æˆ Number
  const num = typeof index === 'string' ? parseInt(index, 10) : index;

  // æ•°å­—è½¬ä¸­æ–‡
  const chineseNum = numberToChinese(num);

  // æ‹¼æ¥æˆ "ç¬¬Xç«  æ ‡é¢˜" æ ¼å¼
  return `ç¬¬${chineseNum}ç«  ${title.trim()}`;
}

/**
 * æ•°å­—è½¬ä¸­æ–‡ï¼ˆæ”¯æŒ 0~99999ï¼‰
 * @param {number} num - è¦è½¬æ¢çš„æ•°å­—
 * @returns {string} ä¸­æ–‡æ•°å­—ï¼Œå¦‚ "ä¸€"ã€"äºŒåä¸‰"ã€"ä¸€ç™¾é›¶äº”"
 */
function numberToChinese(num) {
  const chineseNums = ["é›¶", "ä¸€", "äºŒ", "ä¸‰", "å››", "äº”", "å…­", "ä¸ƒ", "å…«", "ä¹", "å"];
  const units = ["", "å", "ç™¾", "åƒ", "ä¸‡"];

  // 0~10 ç›´æ¥è¿”å›
  if (num <= 10) return chineseNums[num];

  // 11~19 ç‰¹æ®Šå¤„ç†ï¼ˆå¦‚ "åä¸€"ã€"åäº”"ï¼‰
  if (num < 20) return "å" + (num % 10 === 0 ? "" : chineseNums[num % 10]);

  let result = "";
  const strNum = num.toString();

  // ä»é«˜ä½åˆ°ä½ä½é€ä½è½¬æ¢
  for (let i = 0; i < strNum.length; i++) {
    const digit = parseInt(strNum[i], 10);
    const unit = units[strNum.length - i - 1];

    if (digit !== 0) {
      result += chineseNums[digit] + unit; // æ‹¼æ¥æ•°å­—å’Œå•ä½ï¼ˆå¦‚ "ä¸‰ç™¾"ï¼‰
    } else if (result[result.length - 1] !== "é›¶") {
      result += "é›¶"; // å¤„ç†é›¶ï¼ˆé¿å…é‡å¤ï¼‰
    }
  }

  // æ¸…ç†å¤šä½™çš„é›¶ï¼ˆå¦‚ "ä¸€ç™¾é›¶" â†’ "ä¸€ç™¾"ï¼‰
  return result.replace(/é›¶+$/, "").replace(/é›¶+/g, "é›¶");
}
