function login_(openBrowser, checkMode) {
    // java.removeCookie("snssdk.com")
    var cookie_ = String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() || {})['手动登录Token']
    // java.toast(cookie_)
    if (cookie_ && cookie_ != "" && !checkMode) {
        if (!source.variableComment) {
            java.toast("请不要重复登录，请先退出登录！")
        } else {
            java.toast("您为填写token登录，请手动移除token后再次登录")
        }
        return false
    }
    if (openBrowser) {
        try {
            java.startBrowserAwait("https://fanqienovel.com/", "登录")
        } catch (e) {
            java.toast(e)
        }
    }
    // java.log("snssdk cookie: " + java.getCookie("snssdk.com") + "will be reomved")
    try {
        cookie.removeCookie("snssdk.com")
    } catch (e) {}
    // java.log(cookie)
    var cookie_ = "sessionid=" + (String(cookie.getKey("fanqienovel.com", "sessionid")) ? String(cookie.getKey("fanqienovel.com", "sessionid")) : (source.getLoginInfoMap() || {})['手动登录Token'])
    let user
    try {
      user = JSON.parse(java.ajax("https://fanqienovel.com/api/user/info/v2," + JSON.stringify({
        method: "GET",
        headers: {
          "Cookie": cookie_
        }
      }))).data.name
     } catch (e) {java.log(e)}
    if (!cookie_ || cookie_ == "sessionid=" || !user) {
        java.toast("未获取到登录凭据，登录失败")
        return false
    }
    java.toast("欢迎 " + user + "\n登录成功！")
    return true
}

