@js:
var u_Index = 0;     // 0-1
// java.log(book)
var u_Types = ['API', 'WEB'];
var v_Index = [];
var v_Names = [];
var c_Array = [];
var book_id = java.hexDecodeToString(result);

function b64Url(item_id) {
    return `data:item_id;base64,${java.base64Encode(item_id)},{"type":"fqnovel"}`;
}

function push_a(a, b, c, d) {
    c_Array.push({
        "ChapterName": a || "",
        "isVolume": b || false,
        "chapterUrl": c ? b64Url(c) : "",
        "ChapterInfo": (d || "").replace(/第.卷：默认 \|/g, "").trim(),
    });
}

function push_b(list) {
    for (let c of list) {
        let c_name = c.volume_name || "第一卷：默认";
        let c_time = java.timeFormatUTC(c.first_pass_time * 1000, 'yyyy-MM-dd HH:mm:ss', 288000000);
        let c_word = c.chapter_word_number + "字";
        let c_info = [c_name, c_time, c_word];
        let v_name = v_Names[v_Names.length - 1];
        if (v_name != c_name) {
        	   // java.log(c_name);
            v_Index.push(c_Array.length);
            v_Names.push(c_name);
            push_a(c_name, true);
        }
        push_a(c.title, false, c.item_id, c_info.join(" | "));
    }
}

//  app接口  ##卷名&时间&字数
switch (String(u_Types[u_Index])) {
    case 'API':
        let data = JSON.parse(java.ajax(xGorgon(
            "directory/all_items",
            "book_id=" + book_id
        )));

        if (data.code == 0) {
            push_b(data.data.item_data_list);
            break;
        }
        // java.log("下架书籍，使用web接口获取目录");



        //  web接口  ##卷名&时间
    case 'WEB':
        let Json, Host = "https://fanqienovel.com";
        if (false) {
            Json = java.ajax(Host + "/page/" + book_id)
                .match(/__INITIAL_STATE__=([^;]+)/)[1];
        } else {
            Json = java.ajax(Host + "/api/reader/directory/detail?bookId=" + book_id);
        }
        // java.log(Json)
        let List = JSON.parse(Json).data.chapterListWithVolume;
        if (List) {
          for (let list of List) {
              v_Index.push(c_Array.length);
              list.map((_, i) => {
                  let c_name = _.volume_name || "第一卷：默认";
                  let c_time = java.timeFormat(_.firstPassTime * 1000);
                  if (!i) push_a(c_name, true);
                  push_a(_.title, false, _.itemId, [c_name, c_time].join(" | "))
              });
          }
          break;
        }
}



// 返回数组
let len = v_Index.length;
// java.log(JSON.stringify(v_Index))
if (len < 2) { // 分卷太少不显示
    for (; len > 0; len--) {
        c_Array.splice(v_Index[len - 1], 1);
    }
} else if (false) { // 显示效果不好看
    for (let a, b, i = 0; i < len; i++) {
        a = v_Index[i];
        b = v_Index[i + 1];
        if (b == undefined) b = len;
        c_Array[a]["ChapterInfo"] = "共 " + (b - a - 1) + " 章";
    }
}

