var Regex = /Just a moment|onloadTurnstileCallback/;
function Ajax(url) {
     const { java } = this;
     let result = java.ajax(url);
     let retryCount = 0;
     const maxRetries = 3;
     while (Regex.test(result) && retryCount < maxRetries) {
      result = java.startBrowserAwait(url, "验证").body();
     }
     return result;
}

