function login() {
	info = JSON.parse(source.getLoginInfo())
	sum=(Number(info["获取数目"])||50)
	concurrency=(Number(info["并发数"])||1)
	data=checkData()
	data['sum']=sum
	data['concurrency']=concurrency
	setv(data,this)
	setInfo('获取数目',sum)
	setInfo('并发数',concurrency)
}
function setData(key,value,_){
	data=_||checkData(this)
	data[key]=value
	if (key=='sortType'){
		data['order']=!data.order
		setv(data,this)
		return data
		}
	setv(data,this)
	return data[key]
}
function setInfo(key,value){
	let data=JSON.parse(source.getLoginInfo()||"{}")
	data[key]=String(value)
	putLoginInfo(JSON.stringify(data))
	}
function putLoginInfo(info){
	try {
		let key=java.androidId()
		let encodeStr=Packages.android.util.Base64.encodeToString(java.createSymmetricCrypto("AES", key).encrypt(info),2)
		cache.put(`userInfo_${source.getKey()}`, encodeStr)
		return true
		}catch(e){
			java.log(e)
			return source.putLoginInfo(info)
		}
	}
function saveProgress(progress,notshow){
	source.put("progress",progress)
	if(!notshow){java.toast(progress)}
	}
