ruleToc.formatJs:
function formatChapter(index, title) {
  // 如果传入的是字符串数字（如 "23"），先转成 Number
  const num = typeof index === 'string' ? parseInt(index, 10) : index;

  // 数字转中文
  const chineseNum = numberToChinese(num);

  // 拼接成 "第X章 标题" 格式
  return `第${chineseNum}章 ${title.trim()}`;
}

/**
 * 数字转中文（支持 0~99999）
 * @param {number} num - 要转换的数字
 * @returns {string} 中文数字，如 "一"、"二十三"、"一百零五"
 */
function numberToChinese(num) {
  const chineseNums = ["零", "一", "二", "三", "四", "五", "六", "七", "八", "九", "十"];
  const units = ["", "十", "百", "千", "万"];

  // 0~10 直接返回
  if (num <= 10) return chineseNums[num];

  // 11~19 特殊处理（如 "十一"、"十五"）
  if (num < 20) return "十" + (num % 10 === 0 ? "" : chineseNums[num % 10]);

  let result = "";
  const strNum = num.toString();

  // 从高位到低位逐位转换
  for (let i = 0; i < strNum.length; i++) {
    const digit = parseInt(strNum[i], 10);
    const unit = units[strNum.length - i - 1];

    if (digit !== 0) {
      result += chineseNums[digit] + unit; // 拼接数字和单位（如 "三百"）
    } else if (result[result.length - 1] !== "零") {
      result += "零"; // 处理零（避免重复）
    }
  }

  // 清理多余的零（如 "一百零" → "一百"）
  return result.replace(/零+$/, "").replace(/零+/g, "零");
}
