	  const { source,java } = _ || this;
    let infomap = String(source.getLoginHeader()).replace(/^#/,'');
    infomap = (infomap!="null"&&infomap!=""&&infomap!=null)?infomap:"{}";
    return JSON.parse(infomap);
	}

function setHeaders(){
   	const { source,java } = this;
   	let map = getHeaderMap(this);
   	for(let i=0;i<arguments.length;i++){
   		let k =arguments[i].split("=")[0];
   		let v =arguments[i].split("=")[1];
   		map[k] = v;
   		}
  source.putLoginHeader("#"+JSON.stringify(map))
	}
